insert into alternativa(id, descricao, perguntaId,numero,ordem) values (659,'Reconhece que se sente muito triste por não ter mais nenhum amigo, não consegue lidar com as demandas diárias e se mantém estressado, com a qualidade de sono ruim e não se sente capaz de encontrar nenhuma alternativa para essa situação. Não tem muito o que fazer.',196,1,1)
